import React from 'react';

const SlideText = ({ text, textColor }) => {
  if (!text || text.length === 0) {
    return null;
  }

  return (
    <>
      {text.map((textItem, index) => (
        <div
          key={index}
          className="slide-text"
          style={{
            top: `${textItem.y}px`,
            left: `${textItem.x}px`,
            fontSize: `${textItem.fontSize || 16}px`,
            color: textColor || 'white'
          }}
        >
          {textItem.content}
        </div>
      ))}
    </>
  );
};

export default SlideText;
